mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT ST_CODE,ST_NAME,ST_ADDRESSBASIC,ST_ADDRESSDETAIL, ST_ADDRESSEXTRA,ST_PHNO,ST_STARTTIME,ST_ENDTIME,ST_CONTENT,ST_AVGSCORE FROM ST_DATA
ORDER BY ST_CODE DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

